const initRulerFn=()=>{let t=0,e=[];const i=document.querySelector(".top-ruler"),l=document.querySelector(".bottom-ruler"),n=document.querySelector(".left-ruler"),r=document.querySelector(".right-ruler"),o=i.scrollWidth;for(;t<=o;)t%50==0?e.push(`<div class="tick tick-label" style="left: ${t}px;">${t}</div>`):t%10==0?e.push(`<div class="tick tick-major" style="left: ${t}px;"></div>`):t%5==0&&e.push(`<div class="tick tick-minor" style="left: ${t}px;"></div>`),t+=5;for(i.innerHTML=e.join(""),l.innerHTML=e.join(""),e.length=0,t=0;t<=n.scrollHeight;)t%50==0?e.push(`<div class="tick tick-label" style="top: ${t}px;">${t}</div>`):t%10==0?e.push(`<div class="tick tick-major" style="top: ${t}px;"></div>`):t%5==0&&e.push(`<div class="tick tick-minor" style="top: ${t}px;"></div>`),t+=5;n.innerHTML=e.join(""),r.innerHTML=e.join("")};initRulerFn(),window.onresize=initRulerFn,window.joppInit=t=>{window.onresize=null;const{pdfInfo:e}=t,i=document.querySelector(".ruler-container");i.style.width=e.width+"px",i.style.height=e.height+"px",initRulerFn()};