const headerEl=document.querySelector(".header"),bodyEl=document.querySelector(".body"),footerEl=document.querySelector(".footer");window.joppInit=async function(e){const{templateInfo:o,documentInfo:t,pdfInfo:d}=e;document.body.style.display="block";new Date;switch(headerEl.classList.add("hide"),bodyEl.classList.add("hide"),footerEl.classList.add("hide"),o.region){case"header":{headerEl.classList.remove("hide");const e=headerEl.querySelector(".page-link");e.innerText=t.hostname,e.href=t.url;break}case"footer":footerEl.classList.remove("hide");document.querySelector(".date").innerText=(new Date).toLocaleString();break}};